import streamlit as st
import pandas as pd
import numpy as np
# make a title
st.title("üè† Title")                                                                                              
# make a header
st.header("üë®‚Äçüî¨‚öóÔ∏è Header üî¨")
# make a sub header
st.subheader("sub header")

# make a random data
data = np.random.randn(10,1)

# get data in to dataframe
df = pd.DataFrame({
'ID': [1,2,3,4],
'Sentence' : ['This','is','a','example'],
})
st.write(df)

# make a score card
st.metric(label="Name", value="70 ¬∞F", delta="1.2 ¬∞F")

# make layout (sidebar)
with st.sidebar:
  st.title("This is sidebar title")
  st.header("This is sidebar header")
  add_selectbox = st.selectbox("This is select box",("option1","option2","option3"))
  add_radio = st.radio(
        "This is a radio",
        ("option1", "option2")
    )

# make layout (column)
col1,col2 = st.columns([3,1])
col1.write("mention in col1 with 3x of col2")
col2.write("mention in col2")
col2.write(data)

# make layout (tab)
tab1, tab2, tab3 = st.tabs(["Tab1", "Tab2", "Tab3"])

with tab1:
   st.header("tab1")
   st.write("This is not a image")
   st.image("https://www.soitron.com/robots-take-the-robot-out-of-the-human/", width=200) 

with tab2:
   st.header("tab2")
   st.write("This is a image")
   st.image("data:image/jpeg;base64,", width=200)

# make a expander
expander = st.expander("See explanation")
expander.write("""
    The chart above shows some numbers I picked for you.
    I rolled actual dice for these, so they're *guaranteed* to
    be random.
""")
expander.image("https://static.streamlit.io/examples/dice.jpg")


# make container
add_container = st.container()
with add_container:
  st.write("this is container")
  st.bar_chart(data)


